import pytest

from badges import Badge, utils


def test_repr():
    badge = Badge("GitHub", url="https://example.org")
    assert "<Badge object 'GitHub'>" == str(badge)


def test_properties_url_styles():
    # ToDo: change this once #8 is implemented
    # should be `url + "?styel=.."`
    url = "https://example.org"
    b = Badge("GitHub", url)
    assert url + "&style=plastic" == b.url_plastic
    assert url + "&style=flat" == b.url_flat
    assert url + "&style=flat-square" == b.url_flat_square
    assert url + "&style=for-the-badge" == b.url_for_the_badge
    assert url + "&style=social" == b.url_social


@pytest.mark.parametrize(
    ("style"), ["plastic", "flat", "flat-square", "for-the-badge", "social"]
)
def test_get_url(style):
    # ToDo: change this once #8 is implemented
    # should be `url + "?styel=.."`
    url = "https://example.org"
    badge = Badge("GitHub", url)
    assert url + f"&style={style}" == badge.get_url(style)


