def test_get_svg(style, svg):
    badge = Badge(
        "GitHub",
        "https://img.shields.io/static/v1?logo=github&label=&labelColor=gray&message=GitHub&color=%230D0D0D",  # noqa: E501
    )
    assert svg == badge.get_svg(style)
